// Get the current submission from the loop
const submission = items[0].json;

// Check if this is a text submission or file submission
const hasText = submission.submissionText && submission.submissionText.trim() !== '';
const hasFiles = submission.submissionFiles && submission.submissionFiles !== '[]';

// For now, only process text submissions (files need different handling)
if (!hasText) {
  return [{ 
    json: { 
      skip: true,
      message: 'Skipping - no text submission or has files only',
      submissionId: submission.submissionId,
      userId: submission.userId
    } 
  }];
}

// Return clean data for AI Agent INCLUDING rubric criteria
return [{
  json: {
    assignmentId: submission.assignmentId,
    assignmentName: submission.assignmentName,
    submissionId: submission.submissionId,
    userId: submission.userId,
    maxGrade: submission.maxGrade,
    instructions: submission.instructions,
    submissionText: submission.submissionText,
    gradingStatus: submission.gradingStatus,

    // Add rubric criteria
    criterion1Name: submission.criterion1Name || 'definition',
    criterion1Levels: submission.criterion1Levels || '{"wrong":5,"poor definition":20,"good definition":50}',
    criterion2Name: submission.criterion2Name || 'used for',
    criterion2Levels: submission.criterion2Levels || '{"wrong usage":5,"Partially right":20,"right":50}'
  }
}];

// Get the AI Agent output
const aiOutput = items[0].json.output;

// Parse the JSON string
let parsed;
try {
  let cleanOutput = aiOutput.trim();
  cleanOutput = cleanOutput.replace(/```json\n?/g, '');
  cleanOutput = cleanOutput.replace(/```\n?/g, '');
  cleanOutput = cleanOutput.trim();

  parsed = JSON.parse(cleanOutput);
} catch (error) {
  console.error('Failed to parse AI output:', error);
  return [{
    json: {
      error: 'Failed to parse AI response',
      rawOutput: aiOutput
    }
  }];
}

// Get the original data from the node BEFORE AI Agent
// Find the node name - it might be "Prepare Data" or "Code in JavaScript"
const originalData = $('Loop Over Items').last().json;

// Format detailed feedback with rubric breakdown
let detailedFeedback = "=== Rubric Grading ===\n\n";
detailedFeedback += "1. " + (originalData.criterion1Name || "Criterion 1") + ": " + parsed.criterion1Score + " points (" + parsed.criterion1Level + ")\n";
detailedFeedback += "   " + parsed.criterion1Feedback + "\n\n";
detailedFeedback += "2. " + (originalData.criterion2Name || "Criterion 2") + ": " + parsed.criterion2Score + " points (" + parsed.criterion2Level + ")\n";
detailedFeedback += "   " + parsed.criterion2Feedback + "\n\n";
detailedFeedback += "Total Score: " + parsed.totalScore + "/" + (originalData.maxGrade || 100) + "\n\n";
detailedFeedback += "Overall: " + parsed.overallFeedback;

return [{
  json: {
    grade: parsed.totalScore,
    feedback: detailedFeedback,
    criterion1Score: parsed.criterion1Score,
    criterion1Level: parsed.criterion1Level,
    criterion2Score: parsed.criterion2Score,
    criterion2Level: parsed.criterion2Level,
    assignmentId: originalData.assignmentId,
    userId: originalData.userId
  }
}];
